    const {id, productName, productImage, price,info, inCart,total} = props.data;
    const {cartItems, addToCart, removeFromCart, UpdateCart} = useContext(CatalogContext);

  return (
    <div className="product">
      <img src={productImage} />
      <div className="description">
            <p>
                <b>{productName}</b>
            </p>
            <p> ${price}</p>
            <div className="countHandler">
          <button onClick={()=>{removeFromCart(id)}}>
            -
